var calculator = [];


function changeData(val,cat,sportActive,sportsLabel){
	if(calculator[cat] == val.toString()){
		return false;
	}else{
		claulatorClear();
		$($("."+cat)[calculator[cat]]).removeClass('active');
		$($("."+cat)[val]).addClass('active');
		calculator[cat] = val;
		var currentArr = calculatorData[val];
		switch(cat){
			case 'calculatorGroup':
				calculator['calculatorAge']=null;
				$(".calculatorAge").removeClass('active');
				$('#calculatorStep2').removeClass('on');
				$('#calculatorStep3').removeClass('on');
				calculator['calculatorWorkout']="";
				$(".calculatorWorkout").removeClass('active');
				$('#calculatorStep4').removeClass('on');
				calculator['calculatorWorkoutTime']="";
				$(".calculatorWorkoutTime").removeClass('active');
				
				var calcList = $('#calculatorStep2 .calcListT1');
				$(calcList).html("");
				calculator['sportActive']= sportActive;
				calculator['sportsLabel']= sportsLabel;
				if(currentArr.length>1){
					calculator['calculatorResultTemp']=currentArr;					
					for(var i=0; i<currentArr.length;i++){
						var liCalc =  $("<li/>", {
							"class" : "calcItemT1"
						  }).appendTo(calcList);
						var btnCalc = $("<button/>",{
							"class" : "calcItemBtn calculatorAge",
							"data-val":i,
							"data-cat":'calculatorAge',

						}).appendTo(liCalc).click(function() {
								changeData($(this).data('val'),'calculatorAge');
							});
						var imageCalc = $("<div/>",{
							"class":"calcImage"
						}).appendTo(btnCalc);
						$("<img/>",{
							"src":currentArr[i]["image"],
							"alt":currentArr[i]["alt"]
						}).appendTo(imageCalc);
						$("<div/>",{
							"class":"calcText",
							"text":currentArr[i]["text"]
						}).appendTo(btnCalc);
					}
					$('#calculatorStep2').addClass('on');
					zenscroll.intoView(document.getElementById('calculatorStep2'));
				}else{
					calculator['calculatorResult']=currentArr[0];
					$('#calculatorStep3').addClass('on');
					zenscroll.intoView(document.getElementById('calculatorStep3'));	
				}
				

				
			break;
			case 'calculatorAge':
				$('#calculatorStep3').removeClass('on');
				calculator['calculatorWorkout']=null;
				$(".calculatorWorkout").removeClass('active');								
				$('#calculatorStep4').removeClass('on');
				calculator['calculatorWorkoutTime']=null;
				$(".calculatorWorkoutTime").removeClass('active');
				
				calculator['calculatorResult']=calculator['calculatorResultTemp'][val];
				if(calculator['sportActive']=='yes'){
					$('#calculatorStep3').addClass('on');
					zenscroll.intoView(document.getElementById('calculatorStep3'));					
				}else{
					$('#calculatorStep5').addClass('on');
					zenscroll.intoView(document.getElementById('calculatorStep5'));

				}

			break;
			case 'calculatorWorkout':
				$('#calculatorStep4').removeClass('on');
				calculator['calculatorWorkoutTime']=null;
				$(".calculatorWorkoutTime").removeClass('active');
				
				if(val == '1'){//answer NO
					$('#calculatorStep5').addClass('on');
					zenscroll.intoView(document.getElementById('calculatorStep5'));					
				}else{//answer YES
					$('#calculatorStep4').addClass('on');
					zenscroll.intoView(document.getElementById('calculatorStep4'));	
				}
			break;
			case 'calculatorWorkoutTime':
				$('#calculatorStep5').addClass('on');
				zenscroll.intoView(document.getElementById('calculatorStep5'));	
			break;
				
		}
				
	}

	
}

function calculatorShowResult(){
	
	if(calculator['sportActive'] == 'yes'){
		if(calculator['calculatorWorkout'] == '1'){//answer NO
			console.log(calculator['sportsLabel']);
			$('.calcResultText').html(calculator['calculatorResult']['result1']);
			console.log(calculator['sportsLabel']);
			if(calculator['sportsLabel'] == 'yes'){				
				$('.calcLabel').addClass('on');	
			}

		}else{//answer YES
			if(calculator['calculatorWorkoutTime'] == '1'){//answer MORE THEN ONE HOUR
				$('.calcResultText').html(calculator['calculatorResult']['result1']);
				$('.calcResultList').addClass('on');
			}else{//answer MAX ONE HOUR
				$('.calcResultText').html(calculator['calculatorResult']['result2']);				
			}

		}
		
	}else{
		$('.calcResultText').html(calculator['calculatorResult']['result1']);		
	}
	$('#calculatorResult').addClass('on');
	zenscroll.intoView(document.getElementById('calculatorResult'));	
}
function claulatorClear(){
	$('#calculatorResult').removeClass('on');
	$('.calcResultList').removeClass('on');
	$('#calculatorStep5').removeClass('on');
	$('.calcLabel').removeClass('on');
}

